// Filtrar apenas mensagens da sessão atual se houver campo sessao
// Calcular duração da sessão baseado em timestamps ou número de mensagens
const sessaoAtual = parseInt($('Webhook2').first().json.body.sessao);
const rows = items.map(i => i.json);

let historico = `## Histórico da Sessão ${sessaoAtual}\n\n`;
let primeiraMensagemTime = null;
let ultimaMensagemTime = null;
let mensagensCount = 0;

for (const row of rows) {
	if (!row.message) continue;

	// Se a tabela tiver campo sessao, filtrar apenas mensagens desta sessão
	// Caso contrário, incluir todas (compatibilidade com tabelas antigas)
	if (row.sessao !== undefined && row.sessao !== null) {
		const rowSessao = parseInt(row.sessao);
		if (rowSessao !== sessaoAtual) {
			continue; // Pular mensagens de outras sessões
		}
	}

	// Tentar obter timestamp da mensagem (created_at, timestamp, ou similar)
	const timestamp = row.created_at || row.timestamp || row.message.timestamp || null;
	if (timestamp) {
		const msgTime = new Date(timestamp).getTime();
		if (!primeiraMensagemTime || msgTime < primeiraMensagemTime) {
			primeiraMensagemTime = msgTime;
		}
		if (!ultimaMensagemTime || msgTime > ultimaMensagemTime) {
			ultimaMensagemTime = msgTime;
		}
	}

	const tipo = row.message.type;
	const conteudo = (row.message.content || "").trim();

	if (tipo === "human" || tipo === "user") {
		historico += `mensagem paciente: ${conteudo}\n\n`;
		mensagensCount++;
	}

	if (tipo === "ai" || tipo === "assistant") {
		historico += `mensagem terapeuta: ${conteudo}\n\n`;
		mensagensCount++;
	}
}

// Calcular duração da sessão em minutos
let duracaoMinutos = null;
if (primeiraMensagemTime && ultimaMensagemTime) {
	const duracaoMs = ultimaMensagemTime - primeiraMensagemTime;
	duracaoMinutos = Math.round(duracaoMs / (1000 * 60));
} else if (mensagensCount > 0) {
	// Estimativa: assumir ~2 minutos por troca de mensagens (paciente + terapeuta)
	// Isso é uma estimativa conservadora quando não temos timestamps
	duracaoMinutos = Math.round((mensagensCount / 2) * 2);
}

return [
	{
		json: { 
			historico,
			duracaoMinutos: duracaoMinutos,
			mensagensCount: mensagensCount
		}
	}
];

