const docIdMatch = docLink.match(/\/d\/([a-zA-Z0-9-_]+)/);
const docId = docIdMatch ? docIdMatch[1] : '';

if (!docId || docId.startsWith('PLACEHOLDER')) {
  throw new Error(`Link não encontrado para ${diagnostico} sessão ${sessao}`);
}

return {
  json: {
    ...$input.first().json,
    docLink: docLink,
    docId: docId,
    diagnostico: diagnostico,
    sessao: sessao
  }
};

const conteudo = $input.first().json.content || $input.first().json.text || $input.first().json.body || '';

// Remover formatação markdown excessiva
let formatado = conteudo
  .replace(/\*\*/g, '') // Remove negrito
  .replace(/\*/g, '') // Remove itálico
  .replace(/#{1,6}\s/g, '') // Remove headers markdown
  .replace(/\[([^\]]+)\]\([^\)]+\)/g, '$1') // Remove links markdown
  .replace(/\n{3,}/g, '\n\n') // Remove múltiplas quebras de linha
  .trim();

// Extrair seções principais
const objetivosMatch = formatado.match(/Objetivos da Sessão[\s\S]*?(?=Procedimentos|Tarefas|$)/i);
const protocoloMatch = formatado.match(/Procedimentos e Protocolo[\s\S]*?(?=Tarefas|$)/i);
const tarefasMatch = formatado.match(/Tarefas de Casa[\s\S]*?(?=Observações|$)/i);

return {
  json: {
    ...$('Map Links Sessao1').item.json,
    conteudoFormatado: formatado,
    objetivos: objetivosMatch ? objetivosMatch[0].trim() : '',
    protocolo: protocoloMatch ? protocoloMatch[0].trim() : '',
    tarefas: tarefasMatch ? tarefasMatch[0].trim() : ''
  }
};
